var passwd="";function start(){if(passwd==""){passwd=$("#passwd").val()}infoStart();dynStart();$("#btn_Start").attr("disabled",true)}function dynStart(){$.ajax({url:"api",type:"GET",async:true,data:{passwd:passwd,command:"dyn"},timeout:5000,dataType:"json",success:function(j,d,p){var f=j.Uptime;var k=j.CPU;var o=j.Mem;var e=j.Swap;var n=j.Net;var b=j.Disk;$("#cpuUseage").html((100-(k.idle*100)).toFixed(2)+"%");$("#loadAvg").html(f.loadAvg);$("#upTime").html(f.upTime);$("#memUsed .value").html((o.used/1024/1024).toFixed(2)+"MB");$("#memFree .value").html((o.free/1024/1024).toFixed(2)+"MB");$("#memUsage .value").html(o.usage.toFixed(3)+"%");$("#swapUsed .value").html((e.used/1024/1024).toFixed(2)+"MB");$("#swapFree .value").html((e.free/1024/1024).toFixed(2)+"MB");var c=n.rx;var h=n.tx;var m="KB/s";var a="KB/s";if(c>1024){c=c/1024;m="MB/s"}if(h>1024){h=h/1024;a="MB/s"}$("#rx .value").html(c.toFixed(3)+m);$("#tx .value").html(h.toFixed(3)+a);$("#diskList .info").html("");for(var g=0;g<b.length;g++){var l=b[g];$("#diskList .info").append("<div>"+l.name+" 读取:"+parseFloat(l.read).toFixed(2)+"MB/s 写入:"+parseFloat(l.write).toFixed(2)+"MB/s 磁盘使用:"+l.usage+"</div>")}setTimeout(dynStart,1000)},error:function(a,b){$("#btn_Start").attr("disabled",false)}})}function infoStart(){$.get("api?passwd="+passwd+"&command=info",function(a){$("#cpuModel").html(a.cpuModel)})};